import React from 'react';

export default function ImagePopup(props) {
	console.log(props);

	return (
		<div className={`popup popup_${props.name} ${props.isOpen ? 'popup_opened' : ''}`} onClick={props.onClose}>
			<div className="popup__pic" onClick={(event) => {event.stopPropagation()}}>
				<button aria-label="Закрыть" type="button" className={`popup__close popup__close_${props.name}`} onClick={props.onClose} ></button>
				<img src={props.link} alt={props.name} className="popup__img" />
				<p className="popup__text">{props.name}</p>
			</div>
		</div>
	)
}
